  constructor(ruta, formB, router, servicioImagenes, modalService, servicioArtistas) {
    this.ruta = ruta;
    this.formB = formB;
    this.router = router;
    this.servicioImagenes = servicioImagenes;
    this.modalService = modalService;
    this.servicioArtistas = servicioArtistas;
    this.artistaRecibido = 0;
